new_search.css:
.search-bar {
    display: flex;
    align-items: center;
    border-radius: 25px; /* Rounded corners */
    background-color: #f0f0f0; /* Light background */
    padding: 10px 20px; /* Padding for the container */
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1); /* Subtle shadow for depth */
    transition: box-shadow 0.3s; /* Smooth shadow transition */
}

.search-bar:hover {
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2); /* Darker shadow on hover */
}

.search-bar input {
    border: none; /* Remove default border */
    outline: none; /* Remove outline */
    background: transparent; /* Transparent background */
    flex: 1; /* Grow to fill available space */
    font-size: 16px; /* Font size */
    padding: 10px; /* Padding inside input */
    border-radius: 20px; /* Rounded corners for input */
}

.search-bar button {
    border: none; /* Remove default border */
    background-color: transparent; /* Transparent background */
    cursor: pointer; /* Pointer cursor */
    font-size: 18px; /* Icon size */
    margin-left: 10px; /* Space between input and button */
    border-radius: 50%; /* Circular button */
    padding: 8px; /* Padding for button */
    transition: background-color 0.3s; /* Smooth background transition */
}

.search-bar button:hover {
    background-color: #e0e0e0; /* Light background on hover */
}

profile.css:
.profile-card {
    width: 300px; /* Width of the card */
    padding: 20px; /* Padding inside the card */
    background-color: #fff; /* Background color */
    border-radius: 15px; /* Rounded corners */
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1); /* Shadow for depth */
    text-align: center; /* Center text */
    margin: 20px auto; /* Center the card in the page */
}

.profile-image {
    width: 100px; /* Width of the profile image */
    height: 100px; /* Height of the profile image */
    border-radius: 50%; /* Make the image circular */
    object-fit: cover; /* Crop image to fit */
    border: 3px solid #007bff; /* Border color */
}

.profile-name {
    font-size: 24px; /* Font size for name */
    margin: 10px 0; /* Margin above and below name */
}

.profile-bio {
    font-size: 16px; /* Font size for bio */
    color: #666; /* Text color for bio */
}

scroll.css:
.scroll-container {
    width: 300px;
    overflow-x: auto; /* Horizontal scroll */
    white-space: nowrap; /* Prevent line break for inline elements */
    border: 1px solid #ccc;
    padding: 10px;
}

.scroll-content p {
    display: inline-block;
    padding: 10px;
    background-color: lightblue;
    margin-right: 10px;
    border-radius: 5px;
}

Form.css:
/* Dark-themed container for the login form */
.epic-dark-login-container {
    background: rgba(255, 255, 255, 0.1);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    max-width: 400px;
    width: 100%;
    margin: auto;
    background-color: rgba(0, 0, 0, 0.6);
}

/* Form layout and title */
.epic-dark-login-form {
    display: flex;
    flex-direction: column;
}

.epic-dark-login-form h2 {
    color: #f0f0f0;
    margin-bottom: 20px;
    text-align: center;
    font-size: 28px;
    font-weight: bold;
}

/* Input fields with dark style */
.epic-dark-input-group {
    margin-bottom: 20px;
}

.epic-dark-input-group input {
    width: 100%;
    padding: 12px;
    border: none;
    border-radius: 8px;
    background: rgba(255, 255, 255, 0.1);
    color: #fff;
    font-size: 16px;
    outline: none;
    transition: all 0.3s ease;
}

.epic-dark-input-group input::placeholder {
    color: rgba(255, 255, 255, 0.6);
}

.epic-dark-input-group input:focus {
    background: rgba(255, 255, 255, 0.2);
    box-shadow: 0 0 10px rgba(255, 255, 255, 0.2);
}

/* Submit button */
.epic-dark-login-form button {
    padding: 12px;
    background: linear-gradient(135deg, #ff416c, #ff4b2b);
    border: none;
    border-radius: 8px;
    color: #fff;
    font-size: 16px;
    cursor: pointer;
    transition: background 0.3s ease, box-shadow 0.3s ease;
}

.epic-dark-login-form button:hover {
    background: linear-gradient(135deg, #ff4b2b, #ff416c);
    box-shadow: 0 4px 12px rgba(255, 75, 43, 0.5);
}

/* Additional options with dark theme */
.epic-dark-additional-options {
    display: flex;
    justify-content: space-between;
    margin-top: 20px;
}

.epic-dark-additional-options a {
    color: rgba(255, 255, 255, 0.6);
    text-decoration: none;
    transition: color 0.3s ease;
}

.epic-dark-additional-options a:hover {
    color: #fff;
}
